import { useState } from "react";
import useInput2 from "./hooks/use-input2";

const BasicForm = (props) => {
  const {
    value: enteredName,
    isValid: enteredNameIsValid,
    valueChangeHandler: nameChangeHandler,
    nameInputBluerHandler: nameBlurHandler,
    reset: resetNameInput
  } = useInput2((value) => value.trim() !== "");

  let formIsValid = false;

  if (enteredNameIsValid) {
    formIsValid = true;
  } else {
    formIsValid = false;
  }

  const formSubmitHandler = (event) => {
    event.preventDefault();

    if (enteredNameIsValid) {
      return;
    }

    console.log(enteredName);

    resetNameInput();
  };

  const nameInputClasses = nameInputIsValid ? "form-control invalid" : "form-control";
  console.log(nameInputClasses, enteredNameIsValid + " ebteredNameIsValid");

  return (
    <form onSubmit={formSubmitHandler}>
      <div className="control-group">
        <div className={nameInputClasses}>
          <label htmlFor="name">First Name</label>
          <input type="text" id="name" onChange={nameInputChangeHandler} onBlur={nameInputBluerHandler} />
          {nameInputIsValid && <p className="error-text">Name must not be empty</p>}
        </div>
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input type="text" id="name" />
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input type="text" id="name" />
      </div>
      <div className="form-actions">
        <button disabled={formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
