import { useState } from "react";

const BasicForm = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [enteredNameIsValid, setEnteredNameIsValid] = useState(false);
  const [enteredNameIsTouched, setEnteredNameIsTouched] = useState(false);

  const nameInputIsValid = enteredNameIsValid && enteredNameIsTouched;

  const nameInputChangeHandler = (event) => {
    setEnteredName(event.target.value);

    if (event.target.value.trim() === "") {
      setEnteredNameIsValid(true);
    }
  };

  const nameInputBluerHandler = (event) => {
    setEnteredNameIsTouched(true);

    if (event.target.value.trim() === "") {
      setEnteredNameIsValid(true);
    }
  };

  const formSubmitHandler = (event) => {
    event.preventDefault();

    if (enteredName.trim() === "") {
      setEnteredNameIsValid(true);
      return;
    }

    console.log(enteredName);

    setEnteredName("");
    setEnteredNameIsTouched(true);
  };

  const nameInputClasses = enteredNameIsValid ? "form-control invalid" : "form-control";

  return (
    <form onSubmit={formSubmitHandler}>
      <div className="control-group">
        <div className={nameInputClasses}>
          <label htmlFor="name">First Name</label>
          <input type="text" id="name" onChange={nameInputChangeHandler} onBlur={nameInputBluerHandler} />
          {nameInputIsValid && <p className="error-text">Name must not be empty</p>}
        </div>
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input type="text" id="name" />
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input type="text" id="name" />
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
