import { useParams, Outlet, Link, Route } from "react-router-dom";
import { Fragment } from "react";

import HighlitedQuote from "../components/quotes/HighlightedQuote";

const DUMMY_QUOTES = [
  { id: "q1", author: "Bory", text: "Learning React is fun!" },
  { id: "q2", author: "Jeong", text: "Learning React is great!" },
  { id: "q3", author: "Max", text: "Learning React is enjoyable!" }
];

const QuoteDetail = (props) => {
  const params = useParams();

  const quote = DUMMY_QUOTES.find((quote) => quote.id === params.quoteId);

  if (!quote) {
    return <p>No quote found!</p>;
  }

  return (
    <Fragment>
      <HighlitedQuote text={quote.text} author={quote.author} />
      <Route path></Route>
      <div className="centered">
        <Link className="btn--flat" to={`/quotes/${params.quoteId}/comments`}>
          Load Comments
        </Link>
      </div>
      <Outlet />
    </Fragment>
  );
};

export default QuoteDetail;
